import React, { useState, useEffect, useRef } from "react";
import io from "socket.io-client";

const socket = io("http://localhost:5000");

export default function ChatApp() {
  const [username, setUsername] = useState("");
  const [roomId, setRoomId] = useState("");
  const [joinedRoom, setJoinedRoom] = useState("");
  const [chat, setChat] = useState([]);
  const [message, setMessage] = useState("");
  const messagesEndRef = useRef(null);

  // ðŸ”¥ Auto scroll to bottom on new messages
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [chat]);

  // ðŸ”¥ Listen for messages
  useEffect(() => {
    socket.on("chat-message", ({ sender, message, timestamp }) => {
      setChat((prev) => [...prev, { sender, message, timestamp }]);
    });

    socket.on("system-message", ({ message, timestamp }) => {
      setChat((prev) => [...prev, { sender: "System", message, timestamp }]);
    });

    return () => {
      socket.off("chat-message");
      socket.off("system-message");
    };
  }, []);

  // ðŸ”¥ Create Room
  const createRoom = () => {
    if (!username.trim()) return;
    socket.emit("create-room", { username });
    setJoinedRoom("new-room"); // replace with server-generated room ID
  };

  // ðŸ”¥ Join Room
  const joinRoom = () => {
    if (!username.trim() || !roomId.trim()) return;
    socket.emit("join-room", { roomId, username });
    setJoinedRoom(roomId);
  };

  // ðŸ”¥ Send Message with timestamp
  const sendMessage = () => {
    if (!message.trim() || !joinedRoom) return;
    const timestamp = new Date().toISOString();
    socket.emit("chat-message", {
      roomId: joinedRoom,
      sender: username,
      message,
      timestamp,
    });
    setMessage("");
  };

  return (
    <div className="w-full h-screen flex flex-col bg-gray-100">
      {/* If not joined room â†’ show welcome screen */}
      {!joinedRoom && (
        <div className="flex-1 flex flex-col items-center justify-center text-center p-4">
          <h1 className="text-3xl font-bold mb-6 animate-bounce text-blue-600">
            Ravi Chatting App ðŸ’¬
          </h1>
          <input
            type="text"
            placeholder="Enter your name"
            className="border p-2 rounded w-full max-w-sm mb-2"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <div className="flex gap-2 w-full max-w-sm">
            <button
              onClick={createRoom}
              className="px-4 py-2 bg-green-600 text-white rounded-lg flex-1"
            >
              Create Room
            </button>
            <input
              type="text"
              placeholder="Room ID"
              className="border p-2 rounded flex-1"
              value={roomId}
              onChange={(e) => setRoomId(e.target.value)}
            />
            <button
              onClick={joinRoom}
              className="px-4 py-2 bg-blue-600 text-white rounded-lg flex-1"
            >
              Join Room
            </button>
          </div>
        </div>
      )}

      {/* If joined room â†’ show chat */}
      {joinedRoom && (
        <>
          {/* Chat Messages */}
          <div className="flex-1 p-4 overflow-y-auto">
            {chat.map((c, i) => {
              const isMe = c.sender === username;
              const time = c.timestamp
                ? new Date(c.timestamp).toLocaleTimeString([], {
                    hour: "2-digit",
                    minute: "2-digit",
                  })
                : "";

              return (
                <div
                  key={i}
                  className={`px-3 py-2 rounded-lg max-w-[80%] mb-2 relative ${
                    isMe
                      ? "bg-blue-500 text-white self-end ml-auto"
                      : c.sender === "System"
                      ? "bg-gray-300 text-gray-800 italic mx-auto text-center"
                      : "bg-green-200 text-gray-800 self-start mr-auto"
                  }`}
                >
                  {c.sender !== "System" && !isMe && (
                    <b className="block">{c.sender}</b>
                  )}
                  <span>{c.message}</span>
                  {time && (
                    <div className="text-xs opacity-70 mt-1 text-right">
                      {time}
                    </div>
                  )}
                </div>
              );
            })}
            <div ref={messagesEndRef} />
          </div>

          {/* Input Box */}
          <div className="p-2 bg-white flex gap-2 border-t w-full pb-[env(safe-area-inset-bottom)]">
            <input
              type="text"
              placeholder="Type a message..."
              className="flex-1 border p-2 rounded"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              onKeyDown={(e) => e.key === "Enter" && sendMessage()}
            />
            <button
              className="px-4 py-2 bg-blue-600 text-white rounded"
              onClick={sendMessage}
            >
              Send
            </button>
          </div>
        </>
      )}
    </div>
  );
}
