import { useState, useEffect } from "react";
import { io } from "socket.io-client";
import { motion } from "framer-motion";

const socket = io("http://localhost:5001");

export default function RoomChat() {
  const [roomId, setRoomId] = useState("");
  const [joinedRoom, setJoinedRoom] = useState("");
  const [players, setPlayers] = useState([]);
  const [chat, setChat] = useState([]);
  const [message, setMessage] = useState("");
  const [username, setUsername] = useState("");
  const [file, setFile] = useState(null);

  const [showRoomId, setShowRoomId] = useState(false);
  const [showPlayers, setShowPlayers] = useState(false);

  useEffect(() => {
    const savedUser = localStorage.getItem("username");
    const savedRoom = localStorage.getItem("joinedRoom");

    if (savedUser) setUsername(savedUser);
    if (savedRoom) {
      setJoinedRoom(savedRoom);
      setRoomId(savedRoom);
      socket.emit("join-room", { roomId: savedRoom, name: savedUser }, (res) => {
        if (!res.success) {
          localStorage.removeItem("joinedRoom");
        }
      });
    }

    socket.on("player-list", (playerList) => setPlayers(playerList));
    socket.on("chat-message", ({ sender, message, file }) =>
      setChat((prev) => [...prev, { sender, message, file }])
    );

    return () => {
      socket.off("player-list");
      socket.off("chat-message");
    };
  }, []);

  const createRoom = () => {
    if (!username.trim()) return alert("Enter your name");
    socket.emit("create-room", { name: username }, (res) => {
      setJoinedRoom(res.roomId);
      setRoomId(res.roomId);
      localStorage.setItem("username", username);
      localStorage.setItem("joinedRoom", res.roomId);
    });
  };

  const joinRoom = () => {
    if (!username.trim() || !roomId.trim()) return alert("Enter name & room ID");
    socket.emit("join-room", { roomId, name: username }, (res) => {
      if (res.success) {
        setJoinedRoom(roomId);
        localStorage.setItem("username", username);
        localStorage.setItem("joinedRoom", roomId);
      } else alert(res.message);
    });
  };

  const leaveRoom = () => {
    if (!joinedRoom) return;
    socket.emit("leave-room", { roomId: joinedRoom, name: username }, (res) => {
      if (res.success) {
        setJoinedRoom("");
        setPlayers([]);
        setChat([]);
        setRoomId("");
        localStorage.removeItem("joinedRoom");
      }
    });
  };

  const sendMessage = () => {
    if ((!message.trim() && !file) || !joinedRoom) return;

    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        socket.emit("chat-message", {
          roomId: joinedRoom,
          sender: username,
          message,
          file: { name: file.name, type: file.type, data: reader.result },
        });
      };
      reader.readAsDataURL(file);
      setFile(null);
    } else {
      socket.emit("chat-message", {
        roomId: joinedRoom,
        sender: username,
        message,
      });
    }

    setMessage("");
  };

  const copyToClipboard = (text, label) => {
    navigator.clipboard.writeText(text);
    alert(`âœ… ${label} copied: ${text}`);
  };

  return (
    <div className="w-full h-screen flex flex-col bg-gray-100">
      {!joinedRoom ? (
        <div className="flex-1 flex flex-col items-center justify-center p-4 w-full text-center">
          {/* Title */}
          <motion.h1
            className="text-3xl font-bold text-blue-600 mb-4"
            initial={{ opacity: 0, y: 6 }}
            animate={{ opacity: 1, y: 0 }}
          >
            Ravi Chatting App
          </motion.h1>

          <input
            type="text"
            placeholder="Enter your name"
            className="border p-2 rounded w-full max-w-md mb-4"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />

          <div className="flex flex-col sm:flex-row gap-3 w-full max-w-md">
            <button
              onClick={createRoom}
              className="px-4 py-2 bg-green-600 text-white rounded-lg w-full sm:w-auto"
            >
              Create Room
            </button>

            <input
              type="text"
              placeholder="Room ID"
              className="border p-2 rounded flex-1"
              value={roomId}
              onChange={(e) => setRoomId(e.target.value)}
            />

            <button
              onClick={joinRoom}
              className="px-4 py-2 bg-blue-600 text-white rounded-lg w-full sm:w-auto"
            >
              Join Room
            </button>
          </div>
        </div>
      ) : (
        <div className="flex flex-col w-full h-full">
          {/* Header */}
          <div className="p-2 bg-gray-200 flex gap-2 w-full">
            <button
              className="px-3 py-1 bg-gray-700 text-white rounded"
              onClick={() => setShowRoomId(!showRoomId)}
            >
              {showRoomId ? "Hide Room ID" : "Show Room ID"}
            </button>
            <button
              className="px-3 py-1 bg-gray-700 text-white rounded"
              onClick={() => setShowPlayers(!showPlayers)}
            >
              {showPlayers ? "Hide Name" : "Show Name"}
            </button>
            <button
              onClick={leaveRoom}
              className="px-3 py-1 bg-red-600 text-white rounded"
            >
              Leave Room
            </button>
          </div>

          {showRoomId && (
            <div className="flex items-center gap-2 p-2 bg-white w-full">
              <div className="bg-gray-200 p-2 rounded flex-1">
                Room ID: {joinedRoom}
              </div>
              <button
                onClick={() => copyToClipboard(joinedRoom, "Room ID")}
                className="px-2 py-1 bg-blue-600 text-white rounded"
              >
                Copy
              </button>
            </div>
          )}

          {/* Chat Messages */}
          <div className="flex-1 overflow-y-auto p-3 bg-gray-50 flex flex-col">
            {chat.map((c, i) => {
              const isMe = c.sender === username;
              return (
                <div
                  key={i}
                  className={`px-3 py-2 rounded-lg max-w-[80%] mb-2 ${
                    isMe
                      ? "bg-blue-500 text-white self-end"
                      : "bg-gray-200 text-gray-800 self-start"
                  }`}
                >
                  {c.sender !== "System" && !isMe && (
                    <b className="block mb-1">{c.sender}</b>
                  )}
                  {c.message && <p>{c.message}</p>}
                  {c.file && (
                    <div className="mt-2">
                      {c.file.type.startsWith("image/") ? (
                        <img
                          src={c.file.data}
                          alt={c.file.name}
                          className="max-w-[200px] rounded"
                        />
                      ) : (
                        <a
                          href={c.file.data}
                          download={c.file.name}
                          className="text-blue-600 underline"
                        >
                          ðŸ“Ž {c.file.name}
                        </a>
                      )}
                    </div>
                  )}
                </div>
              );
            })}
          </div>

          {/* Input + File Upload */}
          <div className="p-2 bg-white flex gap-2 border-t w-full">
            <input
              type="file"
              onChange={(e) => setFile(e.target.files[0])}
              className="border p-1 rounded"
            />
            <input
              type="text"
              placeholder="Type a message..."
              className="flex-1 border p-2 rounded"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
            />
            <motion.button
              className="px-4 py-2 bg-blue-600 text-white rounded"
              onClick={sendMessage}
              whileTap={{ scale: 0.97 }}
            >
              Send
            </motion.button>
          </div>
        </div>
      )}
    </div>
  );
}
